import jwt from 'jsonwebtoken';

/**
 * Generates a new token with payload
 * @param {object} payload
 * @returns {string} signed token
 */
export default (payload) => {
  const { JWT_SECRET } = process.env;

  return jwt.sign(payload, JWT_SECRET);
};

import { string, object } from 'yup';

/** sign in validation schema */
export const loginSchema = object({
  email: string().email('The email must be valid').required('email is requied'),
  password: string().required('password is required'),
});

export const thumbnailSchema = object({
  url: string().url('invalid url').required('url is required'),
});

/**
 * extracts torkn from request header
 * @param {object} req
 * @returns {string} extracted token
 */
export default (req) => {
  const bearer = req.headers.authorization.split(' ');
  return bearer[1];
};

export default (url) => {
  const acceptedMime = ['.jpg', '.jpeg', '.png', '.gif'];
  return !!acceptedMime.find((mime) => url.endsWith(mime));
};

