import { format, createLogger, transports } from 'winston';
import path from 'path';

const {
  combine, colorize, timestamp: timeStamp, prettyPrint, printf,
} = format;

const PROJECTROOT = path.join(__dirname, '..');

const getStackInfo = (stackIndex) => {
  const stacklist = (new Error()).stack.split('\n').slice(3);
  // this code is from google code
  const stackReg = /at\s+(.*)\s+\((.*):(\d*):(\d*)\)/gi;
  const stackReg2 = /at\s+()(.*):(\d*):(\d*)/gi;

  const s = stacklist[stackIndex] || stacklist[0];
  const sp = stackReg.exec(s) || stackReg2.exec(s);

  if (sp && sp.length === 5) {
    return {
      method: sp[1],
      relativePath: path.relative(PROJECTROOT, sp[2]),
      line: sp[3],
      file: path.basename(sp[2]),
      stack: stacklist.join('\n'),
    };
  }

  return undefined;
};

const formatLogArguments = (args) => {
  const newArgs = Array.prototype.slice.call(args);
  const stackInfo = getStackInfo(1);

  if (stackInfo) {
    const calleeStr = `(${stackInfo.relativePath}:${stackInfo.line})`;

    if (typeof args[0] === 'string') {
      // handle string
      newArgs[0] = `${newArgs[0]} ${calleeStr}`;
    } else if (Array.isArray(args[0])) {
      // handle array
      newArgs[0].unshift(calleeStr);
    } else if (typeof args[0] === 'object') {
      // handle error object
      newArgs[0].message = `${newArgs[0].message} ${calleeStr}`;
    }
  }

  return newArgs;
};

const myFormat = printf(({
  level, message, timestamp,
}) => `${timestamp} ${level}: ${message}`);

const logger = createLogger({
  format: combine(
    timeStamp({
      format: 'YYYY-MM-DD h:mm:ssa',
    }),
    prettyPrint({
      colorize: true,
    }),
    colorize({
      colors: {
        info: 'green',
        error: 'red',
        debug: 'blue',
      },
      message: true,
    }),
    myFormat,
  ),
  transports: [new transports.Console()],
});

logger.exitOnError = false;

export function info() {
  logger.info(...formatLogArguments(arguments));
}

export function debug() {
  logger.debug(...formatLogArguments(arguments));
}

export function error() {
  logger.error(...formatLogArguments(arguments));
}
export default logger;

import helpers from '../helpers';

const { thumbnailSchema, error, checkMime } = helpers;

export default async (req, res, next) => {
  const { url } = req.body;

  try {
    await thumbnailSchema.validate({ url: url && url.trim() });

    const isValidMime = checkMime(url);

    if (!isValidMime) {
      return res.status(400).json({
        status: 400,
        errors: ['invalid url'],
      });
    }

    return next();
  } catch (err) {
    error(err);

    return err.name === 'ValidationError'
      ? res.status(400).json({
        status: 400,
        errors: err.errors,
      })
      : res.status(500).json({
        status: 500,
        error: err.message,
      });
  }
};

import signinController from './signin.controller';
import thumbnailController from './thumbnail.controller';

export default {
  signinController,
  thumbnailController,
};

import genrateToken from '../helpers/generateToken';

/**
 * Sign in controller
 * @param {object} req
 * @param {object} res
 * @returns {object} response to user
 */
export default (req, res) => {
  const { email } = req.body;
  const token = genrateToken({ email });

  return res.status(200).json({
    status: 200,
    token,
  });
};

import jimp from 'jimp';
import helpers from '../helpers';

const { error } = helpers;

export default async (req, res) => {
  const { url } = req.body;

  try {
    const originalThumbnail = await jimp.read(url);
    const resizedThumbnail = await originalThumbnail.resize(50, 50).getBase64Async(jimp.AUTO);

    return res.status(200).send(`<img src="${resizedThumbnail}" alt="thumbnail"/>`);
  } catch (err) {
    error(err);

    return res.status(500).json({
      status: 500,
      error: err.message,
    });
  }
};

